mapper.select:
select id, title, content, publish_user_id, publish_time, comment_amount
from simple_blog.tb_article
where id = #{id}

mapper.update:
update simple_blog.tb_article
set title          = #{title},
    content        = #{content},
    publish_time   = #{publishTime},
    comment_amount = #{commentAmount}
where id = #{id};
